mapper.select:
	select 
    
    from product
    where 1=1
    
    
      and expire_Time > '${expireTime}'
    
    
      and USER_TYPE = ${userType}
    
    
      and type = ${type}
    
    
      and title like '%${keyword}%'
    
    order by create_time desc
    
    
       ${page_sql}
